import { h } from '@stencil/core';
import OrderRenderer from '../order/orderRenderer';
export const RevoViewPort = ({ viewports, dimensions, orderRef, registerElement, onScroll }, children) => {
  return [
    h("div", { class: "main-viewport" },
      h("div", { class: "viewports" },
        children,
        h("revogr-scroll-virtual", { class: "vertical", dimension: "row", viewportStore: viewports['row'].store, dimensionStore: dimensions['row'].store, ref: el => registerElement(el, 'rowScroll'), onScrollVirtual: e => onScroll(e.detail) }),
        h(OrderRenderer, { ref: orderRef }))),
    h("revogr-scroll-virtual", { class: "horizontal", dimension: "col", viewportStore: viewports['col'].store, dimensionStore: dimensions['col'].store, ref: el => registerElement(el, 'colScroll'), onScrollVirtual: e => onScroll(e.detail) }),
  ];
};
